import Vue from 'vue';
import Router from 'vue-router';
import LoginForm from '@/components/LoginForm.vue';
import RegistrationForm from '@/components/RegistrationForm.vue';
import WelcomePage from '@/components/WelcomePage.vue';
import ResetPasswordForm from '@/components/ResetPasswordForm.vue';

Vue.use(Router);

const router = new Router({
  routes: [
    { path: '/login', component: LoginForm },
    { path: '/register', component: RegistrationForm },
    { path: '/welcome', component: WelcomePage },
    { path: '/reset-password', component: ResetPasswordForm },
    { path: '*', redirect: 'login' }, 
  ]
});

router.beforeEach((to, from, next) => {
  if (to.path === '/welcome') {
    if (!isLoggedIn()) {
      next('/login');
    } else {
      next();
    }
  } else {
    next();
  }
});

router.afterEach((to, from) => {
  // If the "from" route is "/reset-password" and the "to" route is "/welcome",
  // it means the user just reset their password, and we can show the success message.
  if (from.path === '/reset-password' && to.path === '/welcome') {
    Swal.fire({
      title: 'Success',
      text: 'Password reset successfully.',
      icon: 'success',
    });
  }
});

function isLoggedIn() {
  const authToken = localStorage.getItem('authToken');
  return !!authToken; 
}

export default router;
